SELECT developer.display_name, project_developer.project_id, issue_update.value FROM gros.project_developer JOIN gros.developer ON project_developer.developer_id = developer.id JOIN (SELECT project_id, updated_by, COUNT(*) AS value FROM gros.issue GROUP BY project_id, updated_by) AS issue_update ON project_developer.project_id = issue_update.project_id AND developer.name = issue_update.updated_by ORDER BY developer.display_name, project_developer.project_id;
